//ACTION TYPES
const INCREMENT = "INCREMENT";
const DECREMENT = "DECREMENT";

//ACTION CREATOR
const increment = (value =1) => {
    return{//obje
        type: INCREMENT,
        payload: value

    }
}

const decrement = (value = 1) => ({
    type: DECREMENT,
    payload: value
})

//REDUCER
const countReducer = (count = 0, action) =>{
   switch(action.type){
       case INCREMENT:
           return count + action.payload;
        case DECREMENT:
            return count - action.payload;
       default:
           return count
   }
}

//STORE
